import React, { useState } from 'react';
import './TransactionInput.css';

const SAMPLE_DATA = `Apr 01 WALMART.CA MISSISSAUGA ON 22.08
Apr 03 KLARNA* WALMART CA VANCOUVER BC 45.99
Apr 05 UBER CANADA TORONTO ON 18.50
Apr 07 UBER CANADA/UBEREATS TORONTO ON 32.75
Apr 10 MCDONALD'S #12345 TORONTO ON 12.45
Apr 12 DOORDASH*MCDONALDS TORONTO ON 28.90
Apr 15 AMAZON.CA MISSISSAUGA ON 67.34
Apr 18 SHOPPERS DRUG MART TORONTO ON 23.56
Apr 20 LCBO/RAO #0123 TORONTO ON 45.00
Apr 22 SHELL OIL TORONTO ON 65.00
Apr 25 NETFLIX.COM TORONTO ON 16.99
Apr 28 SPOTIFY TORONTO ON 10.99`;

function TransactionInput({ onProcess, isProcessing }) {
  const [inputText, setInputText] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputText.trim()) {
      onProcess(inputText);
    }
  };

  const handleLoadSample = () => {
    setInputText(SAMPLE_DATA);
  };

  return (
    <div className="transaction-input">
      <h2>üìù Input Your Transactions</h2>
      <p className="subtitle">Paste raw credit card statements or bank transactions</p>

      <form onSubmit={handleSubmit}>
        <textarea
          className="input-textarea"
          placeholder="Paste your raw transactions here...&#10;&#10;Example:&#10;Apr 01 WALMART.CA MISSISSAUGA ON 22.08&#10;Apr 03 UBER CANADA TORONTO ON 18.50"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          rows={12}
          disabled={isProcessing}
        />

        <div className="button-group">
          <button
            type="button"
            className="btn btn-secondary"
            onClick={handleLoadSample}
            disabled={isProcessing}
          >
            üìã Load Sample Data
          </button>

          <button
            type="submit"
            className="btn btn-primary"
            disabled={!inputText.trim() || isProcessing}
          >
            {isProcessing ? 'ü§ñ Processing with AI...' : '‚ú® Process with AI'}
          </button>
        </div>
      </form>

      <div className="info-box">
        <h3>üéØ What happens next:</h3>
        <ul>
          <li>AI extracts date, vendor, amount, and category</li>
          <li>Vendors are cleaned up (e.g., "KLARNA* WALMART" ‚Üí "Walmart")</li>
          <li>Natural language summaries are generated</li>
          <li>Export to Excel with category totals</li>
        </ul>
      </div>
    </div>
  );
}

export default TransactionInput;
