import React, { useState } from 'react';
import './App.css';
import WelcomePage from './components/WelcomePage';
import TransactionInput from './components/TransactionInput';
import TransactionResults from './components/TransactionResults';
import { parseTransactions } from './utils/aiParser';

function App() {
  const [showWelcome, setShowWelcome] = useState(true);
  const [transactions, setTransactions] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);

  const handleGetStarted = () => {
    setShowWelcome(false);
  };

  const handleProcess = (rawText) => {
    setIsProcessing(true);

    // Simulate AI processing delay
    setTimeout(() => {
      const parsed = parseTransactions(rawText);
      setTransactions(parsed);
      setIsProcessing(false);
    }, 1500);
  };

  const handleReset = () => {
    setTransactions([]);
  };

  if (showWelcome) {
    return <WelcomePage onGetStarted={handleGetStarted} />;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸ’³ Smart Expense Tracker</h1>
        <p>AI-powered transaction insights from raw statements</p>
      </header>

      <main className="App-main">
        {transactions.length === 0 ? (
          <TransactionInput onProcess={handleProcess} isProcessing={isProcessing} />
        ) : (
          <TransactionResults transactions={transactions} onReset={handleReset} />
        )}
      </main>
    </div>
  );
}

export default App;
